    public void withdrawal() {

        if (account.getBalance() < Double.parseDouble(txtScreen.getText())) {
            JOptionPane.showMessageDialog(this, "You Do not Have Enough Mony in Your Account!!Try again");
            txtScreen.setText("");
            withdrawal();
        } else {
            History.history("Withdrawal", txtScreen.getText());
            account.setBalance(account.getBalance() - Double.parseDouble(txtScreen.getText()));
            JOptionPane.showMessageDialog(this, "Your New Balance is " + account.getBalance());

        }
    }

    public void depos() {
        History.history("Deposit", txtScreen.getText());

        account.setBalance(account.getBalance() + Double.parseDouble(txtScreen.getText()));
        JOptionPane.showMessageDialog(this, "Your New Balance is " + account.getBalance());

    }


