    private void closing() {

        WindowEvent closing = new WindowEvent(this, WindowEvent.WINDOW_CLOSING);
        Toolkit.getDefaultToolkit().getSystemEventQueue().postEvent(closing);

    }

    private void contin() {
        int dialogButton = JOptionPane.YES_NO_OPTION;
        dialogButton = JOptionPane.showConfirmDialog(null, "Would You Want to continue?", "Confirm", dialogButton);

        if (dialogButton == 0) {
            closing();

            new orders().setVisible(true);

        } else if (dialogButton == 1) {
            closing();
        }
    }
    private void nxtbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nxtbtnActionPerformed
        if (orders.getOpperationType().equals("with")) {
            withdrawal();
        } else if (orders.getOpperationType().equals("depos")) {
            depos();
        }
        contin();

    }//GEN-LAST:event_nxtbtnActionPerformed

